from flask import jsonify

@app.route('/stu/all')
def stu_all():
    # 查询所有学生信息
    stu_list1 = Student.query.all()
    # 因为有多个对象，所以需要使用列表保存，格式为[{},{},...]
    stu_list2 = []
    for stu in stu_list1:
        # 列表中每个元素的格式为{id:***,name:***}
        stu_list2.append({'id': stu.id, 'name': stu.name})
    # 返回结果{stu_list:[]}
    return jsonify(stu_list=stu_list2)

