from flask import request

@app.route('/stu/add', methods=['POST'])
def stu_add():
    # 接收学生姓名、班级编号
    dict1 = request.form
    stu_name = dict1.get('name')
    class_id = dict1.get('class_id')
    # 创建学生对象
    stu = Student()
    stu.name = stu_name
    stu.class_id = int(class_id)
    # 保存到数据表
    db.session.add(stu)
    db.session.commit()
    # 返回结果为用户的编号
    return jsonify(result=stu.id)

